import React, { useEffect, useState } from 'react';
import axios from 'axios';
import RouterModal from './RouterModal'; // קומפוננטת המודל

const RouterTable = () => {
  const [routers, setRouters] = useState([]);
  const [selectedRouter, setSelectedRouter] = useState(null); // הנתב שנבחר
  const [isModalOpen, setIsModalOpen] = useState(false); // האם המודל פתוח

  useEffect(() => {
    axios.get('http://127.0.0.1:5000/api/routers')
      .then((response) => {
        setRouters(response.data);
      })
      .catch((error) => {
        console.error('Error fetching routers:', error);
      });
  }, []);

  const handleRowClick = (router) => {
    setSelectedRouter(router); // הגדרת הנתב שנבחר
    setIsModalOpen(true); // פתיחת המודל
  };

  const handleCloseModal = () => {
    setIsModalOpen(false); // סגירת המודל
    setSelectedRouter(null); // איפוס הנתב שנבחר
  };

  const handleUpdateRouter = (updatedRouter) => {
    // עדכון הנתב ברשימה המקומית
    setRouters((prevRouters) =>
      prevRouters.map((router) =>
        router.id === updatedRouter.id ? updatedRouter : router
      )
    );
    setIsModalOpen(false); // סגירת המודל
  };

  return (
    <div>
     <table className="sticky-table">
  <thead>
    <tr>
      <th>ID</th>
      <th>Name</th>
      <th>IP Address</th>
      <th>Floor</th>
      <th>Building</th>
      <th>Connection Speed</th>
      <th>Ports Count</th>
      <th>Is Stack</th>
      <th>Slots Count</th>
      <th>Actions</th>
    </tr>
  </thead>
  <tbody>
    {routers.map((router) => (
      <tr key={router.id}>
        <td>{router.id}</td>
        <td>{router.name}</td>
        <td>{router.ip_address}</td>
        <td>{router.floor}</td>
        <td>{router.building}</td>
        <td>{router.connection_speed}</td>
        <td>{router.ports_count}</td>
        <td>{router.is_stack ? 'Yes' : 'No'}</td>
        <td>{router.slots_count}</td>
        <td>
          <button onClick={() => handleDelete(router.id)}>Delete</button>
        </td>
      </tr>
    ))}
  </tbody>
</table>

      {isModalOpen && selectedRouter && (
        <RouterModal
          router={selectedRouter}
          onClose={handleCloseModal}
          onUpdate={handleUpdateRouter}
        />
      )}
    </div>
  );
};

export default RouterTable;
