import React from 'react';
import { useNavigate } from 'react-router-dom';

const InteractiveImage = () => {
  const navigate = useNavigate();

  // פונקציה שמפעילה ניווט לפי האזור
  const handleAreaClick = (e, path) => {
    e.preventDefault(); // מונע את פעולת ברירת המחדל של תגית <area>
    navigate(path);
  };

  return (
    <div>
      <h1>לחץ על הבניין הדרוש</h1>
      <img
        src="/img/kanrit.webp"
        alt="Interactive Building"
        useMap="#image-map"
        style={{ maxWidth: '100%', height: 'auto' }}
      />
      <map name="image-map">
        <area
          alt="South"
          title="South"
          coords="834,25,467,596"
          shape="rect"
          href="/south"
          onClick={(e) => handleAreaClick(e, '/south')}
        />
        <area
          alt="North"
          title="North"
          coords="1346,19,1000,596"
          shape="rect"
          href="/north"
          onClick={(e) => handleAreaClick(e, '/north')}
        />
        <area
          alt="Pit"
          title="Pit"
          coords="723,634,1117,995"
          shape="rect"
          href="/pit"
          onClick={(e) => handleAreaClick(e, '/pit')}
        />
      </map>
    </div>
  );
};

export default InteractiveImage;
