import React, { useEffect, useState } from 'react';
import axios from 'axios';

const NetworksPage = () => {
  const [networks, setNetworks] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);

  // פונקציה לעיצוב תאריך
  const formatDate = (dateString) => {
    if (!dateString) return 'Unknown date';
    const options = { year: 'numeric', month: 'long', day: 'numeric' };
    return new Date(dateString).toLocaleDateString(undefined, options);
  };

  // שליפת רשתות מהשרת
  useEffect(() => {
    setIsLoading(true); // התחלת מצב טעינה
    axios
      .get('http://127.0.0.1:5000/api/networks')
      .then((response) => {
        setNetworks(response.data); // שמירת הנתונים שנשלפו
        setIsLoading(false); // סיום מצב טעינה
      })
      .catch((error) => {
        const errorMessage = error.response
          ? error.response.data.error || error.response.statusText
          : 'Failed to connect to the server.';
        console.error('Error fetching networks:', errorMessage);
        setError(errorMessage); // שמירת הודעת השגיאה
        setIsLoading(false); // סיום מצב טעינה גם במקרה של שגיאה
      });
  }, []);

  // מצב טעינה
  if (isLoading) {
    return <div>Loading...</div>;
  }

  // מצב שגיאה
  if (error) {
    return <div style={{ color: 'red' }}>{error}</div>;
  }

  // תצוגת הנתונים
  return (
    <div>
      <h1>Networks</h1>
      {networks.length === 0 ? (
        <p>No networks found.</p>
      ) : (
        <ul>
          {networks.map((network) => (
            <li key={network.id}>
              <strong>{network.name || 'Unnamed Network'}</strong> <br />
              <em>Created At:</em> {formatDate(network.created_at)}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default NetworksPage;
