import React, { useState } from 'react';
import axios from 'axios';

const RouterModal = ({ router, onClose, onUpdate }) => {
  const [formData, setFormData] = useState(router); // ניהול המידע על הראוטר

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSave = () => {
    axios.put(`http://127.0.0.1:5000/api/routers/${router.id}`, formData)
      .then((response) => {
        onUpdate(response.data); // עדכון הנתב בקומפוננטת האב
        onClose(); // סגירת המודל
      })
      .catch((error) => {
        console.error('Error updating router:', error);
      });
  };

  return (
    <div className="modal">
      <div className="modal-content">
        <h2>Edit Router</h2>
        <form>
          <input
            type="text"
            name="name"
            value={formData.name}
            onChange={handleChange}
            placeholder="Name"
          />
          <input
            type="text"
            name="ip_address"
            value={formData.ip_address}
            onChange={handleChange}
            placeholder="IP Address"
          />
          <input
            type="number"
            name="floor"
            value={formData.floor}
            onChange={handleChange}
            placeholder="Floor"
          />
          <input
            type="text"
            name="building"
            value={formData.building}
            onChange={handleChange}
            placeholder="Building"
          />
          <input
            type="text"
            name="connection_speed"
            value={formData.connection_speed}
            onChange={handleChange}
            placeholder="Connection Speed"
          />
          {/* ניתן להוסיף שדות נוספים לפי הצורך */}
        </form>
        <div className="modal-actions">
          <button onClick={handleSave}>Save</button>
          <button onClick={onClose}>Cancel</button>
        </div>
      </div>
    </div>
  );
};

export default RouterModal;
