import React, { useEffect, useState } from 'react';
import axios from 'axios';

const NetworksPage = () => {
  const [networks, setNetworks] = useState([]);
  const [newNetwork, setNewNetwork] = useState('');
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);

  // שליפת כל הרשתות
  useEffect(() => {
    axios
      .get('http://127.0.0.1:5000/api/networks')
      .then((response) => {
        setNetworks(response.data);
        setIsLoading(false);
      })
      .catch((error) => {
        console.error('Error fetching networks:', error);
        setError('Failed to fetch networks');
        setIsLoading(false);
      });
  }, []);

  // הוספת רשת חדשה
  const handleAddNetwork = () => {
    if (!newNetwork) {
      alert('Please enter a network name.');
      return;
    }

    axios
      .post('http://127.0.0.1:5000/api/networks', { name: newNetwork })
      .then((response) => {
        setNetworks((prev) => [...prev, { id: response.data.id, name: newNetwork }]);
        setNewNetwork('');
      })
      .catch((error) => {
        console.error('Error adding network:', error);
        alert('Failed to add network.');
      });
  };

  if (isLoading) return <div>Loading...</div>;
  if (error) return <div>{error}</div>;

  return (
    <div>
      <h1>Networks</h1>
      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Name</th>
          </tr>
        </thead>
        <tbody>
          {networks.map((network) => (
            <tr key={network.id}>
              <td>{network.id}</td>
              <td>{network.name}</td>
            </tr>
          ))}
        </tbody>
      </table>
      <div>
        <input
          type="text"
          value={newNetwork}
          placeholder="New network name"
          onChange={(e) => setNewNetwork(e.target.value)}
        />
        <button onClick={handleAddNetwork}>Add Network</button>
      </div>
    </div>
  );
};

export default NetworksPage;
