import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const RoutersByBuilding = () => {
  const { building } = useParams(); // קבלת שם הבניין מהנתיב
  const [routers, setRouters] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    // קריאה ל-API כדי להביא נתבים עבור הבניין
    axios
      .get(`http://127.0.0.1:5000/api/routers/building/${building}`)
      .then((response) => {
        setRouters(response.data);
        setIsLoading(false);
      })
      .catch((error) => {
        console.error('Error fetching routers:', error);
        setError('Failed to fetch routers for the building.');
        setIsLoading(false);
      });
  }, [building]);

  if (isLoading) return <div>Loading...</div>;
  if (error) return <div>{error}</div>;

  return (
    <div>
      <div style={{ marginBottom: '10px' }}>
        <Link to="/add-router">
          <button>Add Router</button>
        </Link>
      </div>
      <table className="sticky-table">
        <thead>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>IP Address</th>
            <th>Floor</th>
            <th>Building</th>
            <th>Connection Speed</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {routers.map((router) => (
            <tr key={router.id}>
              <td>{router.id || 'N/A'}</td>
              <td>{router.name || 'N/A'}</td>
              <td>{router.ip_address || 'N/A'}</td>
              <td>{router.floor || 'N/A'}</td>
              <td>{router.building || 'N/A'}</td>
              <td>{router.connection_speed || 'N/A'}</td>
              <td>
                <button onClick={() => handleMoreClick(router)}>More</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {isModalOpen && selectedRouter && (
        <RouterModal
          router={selectedRouter}
          onClose={handleCloseModal}
          onUpdate={handleUpdateRouter} // העברת פונקציית עדכון
          onDelete={handleDeleteRouter} // העברת פונקציית מחיקה
        />
      )}
    </div>
  );
};

export default RoutersByBuilding;
