import React, { useState } from 'react';
import axios from 'axios';

const NetworkModal = ({ network, onClose, onUpdate, onDelete }) => {
  const [isEditing, setIsEditing] = useState(false);
  const [editableNetwork, setEditableNetwork] = useState({
    id: network.id || '',
    name: network.name || '',
    description: network.description || '',
    color: network.color || '#FFFFFF',
  });

  const handleSave = async () => {
    try {
      console.log('Saving network:', editableNetwork);
      const response = await axios.put(
        `http://127.0.0.1:5000/api/networks/${editableNetwork.id}`,
        editableNetwork
      );
      console.log('Updated network:', response.data);
      onUpdate(response.data); // עדכון הנתונים בטבלה
      setIsEditing(false);
    } catch (error) {
      console.error('Error updating network:', error);
      alert('Failed to update network. Please try again.');
    }
  };

  const handleDelete = async () => {
    if (window.confirm('Are you sure you want to delete this network?')) {
      try {
        console.log(`Deleting network ID: ${network.id}`);
        await axios.delete(`http://127.0.0.1:5000/api/networks/${network.id}`);
        onDelete(network.id); // עדכון הטבלה לאחר מחיקה
        onClose(); // סגירת המודאל
      } catch (error) {
        console.error('Error deleting network:', error);
        // בדיקה אם השגיאה קשורה לתלות בראוטרים
        if (error.response?.status === 400) {
          alert(
            error.response.data.error ||
              'Cannot delete this network. It has connected routers.'
          );
        } else {
          alert('Failed to delete network. Please try again.');
        }
      }
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setEditableNetwork((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  return (
    <div className="modal">
      <div className="modal-content">
        <h2>{isEditing ? 'Edit Network' : 'Network Details'}</h2>
        {isEditing ? (
          <>
            <div>
              <label>ID:</label>
              <span>{editableNetwork.id}</span>
            </div>
            <div>
              <label>Name:</label>
              <input
                type="text"
                name="name"
                value={editableNetwork.name}
                onChange={handleChange}
                required
              />
            </div>
            <div>
              <label>Description:</label>
              <textarea
                name="description"
                value={editableNetwork.description}
                onChange={handleChange}
              />
            </div>
            <div>
              <label>Color:</label>
              <input
                type="color"
                name="color"
                value={editableNetwork.color}
                onChange={handleChange}
              />
            </div>
            <div className="modal-actions">
              <button onClick={handleSave}>Save</button>
              <button onClick={() => setIsEditing(false)}>Cancel</button>
            </div>
          </>
        ) : (
          <>
            <p>
              <strong>ID:</strong> {network.id}
            </p>
            <p>
              <strong>Name:</strong> {network.name}
            </p>
            <p>
              <strong>Description:</strong> {network.description || 'N/A'}
            </p>
            <p>
              <strong>Color:</strong>{' '}
              <span
                style={{
                  display: 'inline-block',
                  width: '20px',
                  height: '20px',
                  backgroundColor: network.color || '#FFFFFF',
                  border: '1px solid #000',
                }}
              ></span>{' '}
              {network.color || 'N/A'}
            </p>
            <p>
              <strong>Created At:</strong> {network.created_at || 'N/A'}
            </p>
            <div className="modal-actions">
              <button onClick={() => setIsEditing(true)}>Edit</button>
              <button onClick={handleDelete} className="delete-button">
                Delete
              </button>
              <button onClick={onClose}>Close</button>
            </div>
          </>
        )}
      </div>
    </div>
  );
};

export default NetworkModal;
