import React, { useEffect, useState } from 'react';
import axios from 'axios';
import RouterModal from './RouterModal'; // קומפוננטת המודל

const RouterTable = () => {
  const [routers, setRouters] = useState([]);
  const [selectedRouter, setSelectedRouter] = useState(null); // נתב שנבחר
  const [isModalOpen, setIsModalOpen] = useState(false); // מצב המודל

  useEffect(() => {
    // שליפת הנתבים מהשרת
    axios.get('http://127.0.0.1:5000/api/routers')
      .then((response) => {
        setRouters(response.data);
      })
      .catch((error) => {
        console.error('Error fetching routers:', error);
      });
  }, []);

  const handleMoreClick = (router) => {
    setSelectedRouter(router); // הגדרת הנתב שנבחר
    setIsModalOpen(true); // פתיחת המודל
  };

  const handleCloseModal = () => {
    setSelectedRouter(null); // איפוס הנתב
    setIsModalOpen(false); // סגירת המודל
  };

  return (
    <div>
      <table className="sticky-table">
        <thead>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>IP Address</th>
            <th>Floor</th>
            <th>Building</th>
            <th>Connection Speed</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {routers.map((router) => (
            <tr key={router.id}>
              <td>{router.id}</td>
              <td>{router.name}</td>
              <td>{router.ip_address}</td>
              <td>{router.floor}</td>
              <td>{router.building}</td>
              <td>{router.connection_speed}</td>
              <td>
                <button onClick={() => handleMoreClick(router)}>More</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {isModalOpen && selectedRouter && (
        <RouterModal
          router={selectedRouter}
          onClose={handleCloseModal}
        />
      )}
    </div>
  );
};

export default RouterTable;
