import React, { useEffect, useState } from 'react';
import axios from 'axios';

const RouterTable = () => {
  const [routers, setRouters] = useState([]);

  useEffect(() => {
    axios.get('http://127.0.0.1:5000/api/routers')
      .then((response) => {
        setRouters(response.data);
      })
      .catch((error) => {
        console.error('Error fetching routers:', error);
      });
  }, []);

  const handleDelete = (id) => {
    axios.delete(`http://127.0.0.1:5000/api/routers/${id}`)
      .then(() => {
        setRouters(routers.filter((router) => router.id !== id));
        alert('Router deleted successfully!');
      })
      .catch((error) => {
        console.error('Error deleting router:', error);
      });
  };

  return (
    <table>
      <thead>
        <tr>
          <th>ID</th>
          <th>Name</th>
          <th>IP Address</th>
          <th>Floor</th>
          <th>Building</th>
          <th>Connection Speed</th>
          <th>Actions</th>
        </tr>
      </thead>
      <tbody>
        {routers.map((router) => (
          <tr key={router.id}>
            <td>{router.id}</td>
            <td>{router.name}</td>
            <td>{router.ip_address}</td>
            <td>{router.floor}</td>
            <td>{router.building}</td>
            <td>{router.connection_speed}</td>
            <td>
              <button onClick={() => handleDelete(router.id)}>Delete</button>
              {/* ניתן להוסיף כפתור עריכה */}
            </td>
          </tr>
        ))}
      </tbody>
    </table>
  );
};

export default RouterTable;
