import React, { useState } from 'react';
import axios from 'axios';

const AddNetworkPage = () => {
  const [network, setNetwork] = useState({
    name: '',
    description: '',
  });
  const [errorMessage, setErrorMessage] = useState('');

  const handleChange = (e) => {
    const { name, value } = e.target;
    setNetwork({ ...network, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (!network.name) {
      setErrorMessage('Network name is required.');
      return;
    }

    axios
      .post('http://127.0.0.1:5000/api/networks/', network)
      .then((response) => {
        alert('Network added successfully!');
        setNetwork({ name: '', description: '' }); // איפוס הטופס
        setErrorMessage('');
      })
      .catch((error) => {
        console.error('Error adding network:', error);
        setErrorMessage('Failed to add network. Please try again.');
      });
  };

  return (
    <div>
      <h1>Add Network</h1>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Name:</label>
          <input
            type="text"
            name="name"
            value={network.name}
            onChange={handleChange}
            required
            placeholder="Enter network name"
          />
        </div>
        <div>
          <label>Description:</label>
          <textarea
            name="description"
            value={network.description}
            onChange={handleChange}
            placeholder="Enter network description (optional)"
          />
        </div>
        <button type="submit">Add Network</button>
        {errorMessage && <p style={{ color: 'red' }}>{errorMessage}</p>}
      </form>
    </div>
  );
};

export default AddNetworkPage;
