import React, { useState } from 'react';
import axios from 'axios';

const AddRouterPage = () => {
  const [router, setRouter] = useState({
    name: '',
    ip_address: '',
    floor: '',
    building: '',
    connection_speed: '',
  });

  const handleChange = (e) => {
    setRouter({ ...router, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    axios.post('http://127.0.0.1:5000/api/routers', router)
      .then((response) => {
        alert('Router added successfully!');
        setRouter({
          name: '',
          ip_address: '',
          floor: '',
          building: '',
          connection_speed: '',
        });
      })
      .catch((error) => {
        console.error('Error adding router:', error);
      });
  };

  return (
    <div>
      <h1>Add a New Router</h1>
      <form onSubmit={handleSubmit}>
        <input type="text" name="name" placeholder="Name" value={router.name} onChange={handleChange} required />
        <input type="text" name="ip_address" placeholder="IP Address" value={router.ip_address} onChange={handleChange} required />
        <input type="number" name="floor" placeholder="Floor" value={router.floor} onChange={handleChange} required />
        <select name="building" value={router.building} onChange={handleChange} required>
          <option value="">Select Building</option>
          <option value="North">North</option>
          <option value="South">South</option>
          <option value="Pit">Pit</option>
        </select>
        <input type="text" name="connection_speed" placeholder="Connection Speed" value={router.connection_speed} onChange={handleChange} required />
        <button type="submit">Add Router</button>
      </form>
    </div>
  );
};

export default AddRouterPage;
