import React, { useState, useEffect, useMemo, useCallback } from 'react';
import axios from 'axios';
import {
  useReactTable,
  getCoreRowModel,
  getSortedRowModel,
  getFilteredRowModel,
  flexRender,
} from '@tanstack/react-table';
import { useNavigate } from 'react-router-dom';
import RouterModal from './RouterModal';
import './RouterTable.css';

const RouterTable = () => {
  const [routers, setRouters] = useState([]);
  const [networks, setNetworks] = useState([]);
  const [globalFilter, setGlobalFilter] = useState('');
  const [selectedRouter, setSelectedRouter] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchData = async () => {
      setIsLoading(true);
      try {
        const [routersRes, networksRes] = await Promise.all([
          axios.get('http://127.0.0.1:5000/api/routers'),
          axios.get('http://127.0.0.1:5000/api/networks'),
        ]);
        setRouters(routersRes.data);
        setNetworks(networksRes.data);
      } catch (err) {
        setError('Failed to load data from the server');
        console.error(err);
      } finally {
        setIsLoading(false);
      }
    };
    fetchData();
  }, []);

  const getNetworkDetails = useCallback(
    (networkId) => {
      return networks.find((network) => network.id === networkId) || {};
    },
    [networks]
  );

  const data = useMemo(() => {
    return routers.map((router) => {
      const { name: networkName, color: networkColor } = getNetworkDetails(router.network_id);
      return {
        ...router,
        networkName: networkName || 'Unknown',
        networkColor: networkColor || '#FFFFFF',
      };
    });
  }, [routers, getNetworkDetails]);

  const fuzzyFilter = (rows, columnId, filterValue) => {
    return rows.filter((row) => {
      const cellValue = row.getValue(columnId)?.toString().toLowerCase() || '';
      const filterLower = filterValue.toLowerCase();
      return cellValue.includes(filterLower);
    });
  };

  const columns = useMemo(
    () => [
      { accessorKey: 'id', header: 'ID' },
      { accessorKey: 'name', header: 'Name' },
      { accessorKey: 'ip_address', header: 'IP Address' },
      { accessorKey: 'floor', header: 'Floor' },
      { accessorKey: 'building', header: 'Building', filterFn: fuzzyFilter },
      { accessorKey: 'networkName', header: 'Network' },
      {
        id: 'actions',
        header: 'Actions',
        cell: ({ row }) => (
          <button onClick={() => handleMoreClick(row.original)}>More</button>
        ),
      },
    ],
    []
  );

  const table = useReactTable({
    data,
    columns,
    getCoreRowModel: getCoreRowModel(),
    getSortedRowModel: getSortedRowModel(),
    getFilteredRowModel: getFilteredRowModel(),
    state: {
      globalFilter,
    },
    onGlobalFilterChange: setGlobalFilter,
    filterFns: {
      fuzzyFilter,
    },
  });

  const handleMoreClick = (router) => {
    setSelectedRouter(router);
    setIsModalOpen(true);
  };

  const handleCloseModal = () => {
    setSelectedRouter(null);
    setIsModalOpen(false);
  };

  const handleUpdateRouter = (updatedRouter) => {
    setRouters((prev) =>
      prev.map((router) => (router.id === updatedRouter.id ? updatedRouter : router))
    );
    setIsModalOpen(false);
  };

  if (isLoading) return <div>Loading...</div>;
  if (error) return <div style={{ color: 'red' }}>{error}</div>;

  return (
    <div>
      <div className="table-header">
        <input
          type="text"
          placeholder="Global Search..."
          value={globalFilter || ''}
          onChange={(e) => setGlobalFilter(e.target.value)}
          className="global-filter"
        />
        <button
          onClick={() => navigate('/add-router')}
          className="add-router-button"
        >
          Add Router
        </button>
      </div>
      <div className="table-container">
        <table className="router-table">
          <thead>
            {table.getHeaderGroups().map((headerGroup) => (
              <tr key={headerGroup.id}>
                {headerGroup.headers.map((header) => (
                  <th
                    key={header.id}
                    onClick={header.column.getToggleSortingHandler()}
                    style={{ cursor: header.column.getCanSort() ? 'pointer' : 'default' }}
                  >
                    {flexRender(header.column.columnDef.header, header.getContext())}
                    {header.column.getCanSort() && (
                      <span>
                        {header.column.getIsSorted() === 'asc'
                          ? ' ðŸ”¼'
                          : header.column.getIsSorted() === 'desc'
                          ? ' ðŸ”½'
                          : ''}
                      </span>
                    )}
                    {header.column.getCanFilter() && (
                      <input
                        type="text"
                        value={header.column.getFilterValue() || ''}
                        onChange={(e) => header.column.setFilterValue(e.target.value)}
                        placeholder={`Filter ${header.column.columnDef.header}`}
                        className="column-filter"
                      />
                    )}
                  </th>
                ))}
              </tr>
            ))}
          </thead>
          <tbody>
            {table.getRowModel().rows.map((row) => (
              <tr key={row.id} style={{ backgroundColor: row.original.networkColor }}>
                {row.getVisibleCells().map((cell) => (
                  <td key={cell.id}>
                    {flexRender(cell.column.columnDef.cell, cell.getContext())}
                  </td>
                ))}
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {isModalOpen && selectedRouter && (
        <RouterModal
          router={selectedRouter}
          onClose={handleCloseModal}
          onUpdate={handleUpdateRouter}
          onDelete={(id) => {
            setRouters((prev) => prev.filter((router) => router.id !== id));
            setIsModalOpen(false);
          }}
        />
      )}
    </div>
  );
};

export default RouterTable;
