import React, { useEffect, useState } from 'react';
import axios from 'axios';

const NetworkTable = () => {
  const [networks, setNetworks] = useState([]); // שמירת המידע
  const [isLoading, setIsLoading] = useState(true); // טעינה
  const [error, setError] = useState(null); // שגיאות

  useEffect(() => {
    // שליפת המידע מהשרת
    axios
      .get('http://127.0.0.1:5000/api/networks/')
      .then((response) => {
        setNetworks(response.data);
        setIsLoading(false);
      })
      .catch((error) => {
        console.error('Error fetching networks:', error);
        setError('Failed to fetch networks.');
        setIsLoading(false);
      });
  }, []);

  if (isLoading) return <div>Loading...</div>;
  if (error) return <div>{error}</div>;

  return (
    <div>
      <h1>Networks</h1>
      {networks.length === 0 ? (
        <p>No networks available.</p>
      ) : (
        <table>
          <thead>
            <tr>
              <th>ID</th>
              <th>Name</th>
              <th>Description</th>
              <th>Created At</th>
            </tr>
          </thead>
          <tbody>
            {networks.map((network) => (
              <tr key={network.id}>
                <td>{network.id}</td>
                <td>{network.name}</td>
                <td>{network.description || 'N/A'}</td>
                <td>{network.created_at || 'N/A'}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default NetworkTable;
