import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import RouterModal from '../components/RouterModal';

const RoutersByBuilding = () => {
  const { building } = useParams(); // קבלת שם הבניין מהנתיב
  const [routers, setRouters] = useState([]);
  const [selectedRouter, setSelectedRouter] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    // קריאה ל-API כדי להביא נתבים עבור הבניין
    axios
      .get(`http://127.0.0.1:5000/api/routers/building/${building}`)
      .then((response) => {
        setRouters(response.data);
        setIsLoading(false);
      })
      .catch((error) => {
        console.error('Error fetching routers:', error);
        setError('Failed to fetch routers for the building.');
        setIsLoading(false);
      });
  }, [building]);

  // פתיחת מודל עם פרטי נתב
  const handleMoreClick = (router) => {
    setSelectedRouter(router);
    setIsModalOpen(true);
  };

  // סגירת המודל
  const handleCloseModal = () => {
    setSelectedRouter(null);
    setIsModalOpen(false);
  };

  // עדכון נתב ברשימה לאחר עריכה
  const handleUpdateRouter = (updatedRouter) => {
    setRouters((prevRouters) =>
      prevRouters.map((router) =>
        router.id === updatedRouter.id ? updatedRouter : router
      )
    );
    setIsModalOpen(false);
  };

  // מחיקת נתב מהרשימה ומהשרת
  const handleDeleteRouter = (id) => {
    axios
      .delete(`http://127.0.0.1:5000/api/routers/${id}`)
      .then(() => {
        setRouters((prevRouters) =>
          prevRouters.filter((router) => router.id !== id)
        );
        alert('Router deleted successfully!');
        setIsModalOpen(false);
      })
      .catch((error) => {
        console.error('Error deleting router:', error);
        alert('Failed to delete router.');
      });
  };

  if (isLoading) return <div>Loading...</div>;
  if (error) return <div>{error}</div>;

  return (
    <div>
      <h1>Routers in {building} Building</h1>
      <table className="sticky-table">
<thead>
  <tr>
    <th>ID</th>
    <th>Name</th>
    <th>IP Address</th>
    <th>Floor</th>
    <th>Connection Speed</th>
    <th>Network Type</th> {/* עמודה חדשה */}
  </tr>
</thead>
<tbody>
  {routers.map((router) => (
    <tr key={router.id}>
      <td>{router.id}</td>
      <td>{router.name}</td>
      <td>{router.ip_address}</td>
      <td>{router.floor}</td>
      <td>{router.connection_speed}</td>
      <td>{router.network_type || 'N/A'}</td> {/* הצגת סוג הרשת */}
    </tr>
  ))}
</tbody>

      </table>

      {isModalOpen && selectedRouter && (
        <RouterModal
          router={selectedRouter}
          onClose={handleCloseModal}
          onUpdate={handleUpdateRouter} // העברת פונקציית עדכון
          onDelete={handleDeleteRouter} // העברת פונקציית מחיקה
        />
      )}
    </div>
  );
};

export default RoutersByBuilding;
