import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useLanguage } from '../contexts/LanguageContext';

const AddRouterPage = () => {
  const [router, setRouter] = useState({
    name: '',
    model_id: '',
    ip_address: '',
    floor: '',
    building: '',
    connection_speed: '',
    ports_count: 8,
    is_stack: false,
    slots_count: 0,
    network_id: '',
  });

  const [models, setModels] = useState([]); // רשימת דגמים
  const [networks, setNetworks] = useState([]); // רשימת רשתות
  const [addNewModel, setAddNewModel] = useState(false); // האם להוסיף דגם חדש
  const [newModelName, setNewModelName] = useState(''); // שם הדגם החדש
  const [errorMessage, setErrorMessage] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const { translations } = useLanguage();

  // טעינת רשתות ודגמים
  useEffect(() => {
    const fetchData = async () => {
      try {
        const [networksRes, modelsRes] = await Promise.all([
          axios.get('http://127.0.0.1:5000/api/networks/'),
          axios.get('http://127.0.0.1:5000/api/router-models/'),
        ]);
        setNetworks(networksRes.data);
        setModels(modelsRes.data);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };
    fetchData();
  }, []);

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    const finalValue = type === 'checkbox' ? checked : value;
    setRouter((prevRouter) => ({
      ...prevRouter,
      [name]: finalValue,
    }));
    setErrorMessage('');
  };

  const handleModelChange = (e) => {
    const { value } = e.target;
    if (value === 'add_new') {
      setAddNewModel(true);
      setRouter((prevRouter) => ({
        ...prevRouter,
        model_id: '',
      }));
    } else {
      setAddNewModel(false);
      setRouter((prevRouter) => ({
        ...prevRouter,
        model_id: value,
      }));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!router.name.trim() || !router.ip_address.trim()) {
      setErrorMessage(translations.router_name_ip_required || 'Name and IP address are required.');
      return;
    }

    setIsLoading(true);

    try {
      let modelId = router.model_id;

      // אם מתווסף דגם חדש, שמור אותו בבסיס הנתונים
      if (addNewModel && newModelName.trim()) {
        const modelResponse = await axios.post('http://127.0.0.1:5000/api/router-models/', {
          model_name: newModelName,
        });
        modelId = modelResponse.data.id; // קבלת ה-ID של הדגם החדש
      }

      // שמירת הנתב עם ה-model_id
      await axios.post('http://127.0.0.1:5000/api/routers/', {
        ...router,
        model_id: modelId,
      });

      alert(translations.router_added_success || 'Router added successfully!');
      setRouter({
        name: '',
        model_id: '',
        ip_address: '',
        floor: '',
        building: '',
        connection_speed: '',
        ports_count: 8,
        is_stack: false,
        slots_count: 0,
        network_id: '',
      });
      setNewModelName('');
      setAddNewModel(false);
      setErrorMessage('');
    } catch (error) {
      console.error('Error adding router:', error);
      const serverError =
        error.response?.data?.error ||
        translations.router_add_failed ||
        'Failed to add router. Please try again.';
      setErrorMessage(serverError);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div>
      <h1>{translations.add_router || 'Add Router'}</h1>
      <form onSubmit={handleSubmit}>
        <div>
          <label>{translations.name || 'Name'}:</label>
          <input
            type="text"
            name="name"
            value={router.name}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label>{translations.model || 'Model'}:</label>
          <select name="model_id" value={router.model_id} onChange={handleModelChange} required>
            <option value="">{translations.select_model || 'Select Model'}</option>
            {models.map((model) => (
              <option key={model.id} value={model.id}>
                {model.model_name}
              </option>
            ))}
            <option value="add_new">{translations.add_new_model || 'Add New Model'}</option>
          </select>
          {addNewModel && (
            <div>
              <label>{translations.new_model_name || 'New Model Name'}:</label>
              <input
                type="text"
                value={newModelName}
                onChange={(e) => setNewModelName(e.target.value)}
                required
              />
            </div>
          )}
        </div>
        <div>
          <label>{translations.ip_address || 'IP Address'}:</label>
          <input
            type="text"
            name="ip_address"
            value={router.ip_address}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label>{translations.floor || 'Floor'}:</label>
          <input
            type="number"
            name="floor"
            value={router.floor}
            onChange={handleChange}
          />
        </div>
        <div>
          <label>{translations.building || 'Building'}:</label>
          <input
            type="text"
            name="building"
            value={router.building}
            onChange={handleChange}
          />
        </div>
        <div>
          <label>{translations.connection_speed || 'Connection Speed'}:</label>
          <input
            type="text"
            name="connection_speed"
            value={router.connection_speed}
            onChange={handleChange}
          />
        </div>
        <div>
          <label>{translations.ports_count || 'Ports Count'}:</label>
          <input
            type="number"
            name="ports_count"
            value={router.ports_count}
            onChange={handleChange}
          />
        </div>
        <div>
          <label>{translations.is_stack || 'Is Stack'}:</label>
          <input
            type="checkbox"
            name="is_stack"
            checked={router.is_stack}
            onChange={handleChange}
          />
        </div>
        <div>
          <label>{translations.slots_count || 'Slots Count'}:</label>
          <input
            type="number"
            name="slots_count"
            value={router.slots_count}
            onChange={handleChange}
          />
        </div>
        <div>
          <label>{translations.network || 'Network'}:</label>
          <select
            name="network_id"
            value={router.network_id}
            onChange={handleChange}
            required
          >
            <option value="">{translations.select_network || 'Select Network'}</option>
            {networks.map((network) => (
              <option key={network.id} value={network.id}>
                {network.name}
              </option>
            ))}
          </select>
        </div>
        <button type="submit" disabled={isLoading}>
          {isLoading
            ? translations.adding || 'Adding...'
            : translations.add_router || 'Add Router'}
        </button>
        {errorMessage && <p style={{ color: 'red' }}>{errorMessage}</p>}
      </form>
    </div>
  );
};

export default AddRouterPage;
