import React, { useEffect, useState } from 'react';
import axios from 'axios';

const RouterTable = () => {
  const [routers, setRouters] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // שליפת הנתונים מה-API
    axios.get('http://127.0.0.1:5000/api/routers')
      .then((response) => {
        setRouters(response.data);
        setLoading(false);
      })
      .catch((error) => {
        console.error('Error fetching routers:', error);
        setLoading(false);
      });
  }, []);

  if (loading) {
    return <p>Loading...</p>;
  }

  return (
    <table>
      <thead>
        <tr>
          <th>ID</th>
          <th>Name</th>
          <th>IP Address</th>
          <th>Floor</th>
          <th>Building</th>
          <th>Connection Speed</th>
        </tr>
      </thead>
      <tbody>
        {routers.map((router) => (
          <tr key={router.id}>
            <td>{router.id}</td>
            <td>{router.name}</td>
            <td>{router.ip_address}</td>
            <td>{router.floor}</td>
            <td>{router.building}</td>
            <td>{router.connection_speed}</td>
          </tr>
        ))}
      </tbody>
    </table>
  );
};

export default RouterTable;
