import React from 'react';
import { useNavigate } from 'react-router-dom';
import './InteractiveImage.css'; // קובץ CSS לעיצוב

const InteractiveImage = () => {
  const navigate = useNavigate();

  // פונקציה שמפעילה ניווט לפי האזור
  const handleAreaClick = (path) => {
    navigate(path);
  };

  return (
    <div className="interactive-image-container">
      <h1>לחץ על הבניין הדרוש</h1>
      <div className="image-wrapper">
        <img
          src="/img/kanrit.webp"
          alt="Interactive Building"
          className="interactive-image"
        />
        {/* אזורים לחיצים */}
        <div
          className="clickable-area south"
          onClick={() => handleAreaClick('/south')}
          title="South Building"
        ></div>
        <div
          className="clickable-area north"
          onClick={() => handleAreaClick('/north')}
          title="North Building"
        ></div>
        <div
          className="clickable-area pit"
          onClick={() => handleAreaClick('/pit')}
          title="Pit Area"
        ></div>
      </div>
    </div>
  );
};

export default InteractiveImage;
