import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './RouterTable.css'; // הקובץ שבו נשמור את עיצוב ה-CSS

const RouterTable = () => {
  const [routers, setRouters] = useState([]);

  useEffect(() => {
    axios.get('http://127.0.0.1:5000/api/routers')
      .then((response) => {
        setRouters(response.data);
      })
      .catch((error) => {
        console.error('Error fetching routers:', error);
      });
  }, []);

  const handleDelete = (id) => {
    axios.delete(`http://127.0.0.1:5000/api/routers/${id}`)
      .then(() => {
        setRouters(routers.filter((router) => router.id !== id));
        alert('Router deleted successfully!');
      })
      .catch((error) => {
        console.error('Error deleting router:', error);
      });
  };

  return (
    <div className="table-container">
      <table className="sticky-table">
<thead>
  <tr>
    <th>ID</th>
    <th>Name</th>
    <th>IP Address</th>
    <th>Floor</th>
    <th>Building</th>
    <th>Connection Speed</th>
    <th>Network Type</th>
    <th>Ports Count</th>
    <th>Is Stack</th>
    <th>Slots Count</th>
    <th>Network ID</th>
    <th>Actions</th>
  </tr>
</thead>
<tbody>
  {routers.map((router) => (
    <tr key={router.id}>
      <td>{router.id}</td>
      <td>{router.name}</td>
      <td>{router.ip_address}</td>
      <td>{router.floor}</td>
      <td>{router.building}</td>
      <td>{router.connection_speed}</td>
      <td>{router.network_type}</td>
      <td>{router.ports_count}</td>
      <td>{router.is_stack ? 'Yes' : 'No'}</td>
      <td>{router.slots_count}</td>
      <td>{router.network_id}</td>
      <td>
        <button onClick={() => handleDelete(router.id)}>Delete</button>
      </td>
    </tr>
  ))}
</tbody>

      </table>
    </div>
  );
};

export default RouterTable;
