import React, { useEffect, useState } from 'react';
import axios from 'axios';
import RouterModal from './RouterModal'; // קומפוננטת המודל

const RouterTable = () => {
  const [routers, setRouters] = useState([]); // רשימת נתבים
  const [selectedRouter, setSelectedRouter] = useState(null); // נתב שנבחר
  const [isModalOpen, setIsModalOpen] = useState(false); // האם המודל פתוח

  // שליפת הנתבים מהשרת
  useEffect(() => {
    axios.get('http://127.0.0.1:5000/api/routers')
      .then((response) => {
        setRouters(response.data); // עדכון רשימת הנתבים
      })
      .catch((error) => {
        console.error('Error fetching routers:', error);
      });
  }, []);

  // פתיחת מודל עם פרטי הנתב
  const handleMoreClick = (router) => {
    setSelectedRouter(router); // הגדרת הנתב שנבחר
    setIsModalOpen(true); // פתיחת המודל
  };

  // סגירת המודל
  const handleCloseModal = () => {
    setSelectedRouter(null); // איפוס הנתב שנבחר
    setIsModalOpen(false); // סגירת המודל
  };

  // עדכון הנתב ברשימה המקומית לאחר עריכה
  const handleUpdateRouter = (updatedRouter) => {
    setRouters((prevRouters) =>
      prevRouters.map((router) =>
        router.id === updatedRouter.id ? updatedRouter : router
      )
    );
    setIsModalOpen(false); // סגירת המודל
  };

  // מחיקת נתב מהרשימה ומהשרת
  const handleDeleteRouter = (id) => {
    axios.delete(`http://127.0.0.1:5000/api/routers/${id}`)
      .then(() => {
        setRouters((prevRouters) =>
          prevRouters.filter((router) => router.id !== id)
        );
        alert('Router deleted successfully!');
        setIsModalOpen(false); // סגירת המודל
      })
      .catch((error) => {
        console.error('Error deleting router:', error);
      });
  };

  return (
    <div>
      <table className="sticky-table">
        <thead>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>IP Address</th>
            <th>Floor</th>
            <th>Building</th>
            <th>Connection Speed</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {routers.map((router) => (
            <tr key={router.id}>
              <td>{router.id}</td>
              <td>{router.name}</td>
              <td>{router.ip_address}</td>
              <td>{router.floor}</td>
              <td>{router.building}</td>
              <td>{router.connection_speed}</td>
              <td>
                <button onClick={() => handleMoreClick(router)}>More</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {isModalOpen && selectedRouter && (
        <RouterModal
          router={selectedRouter}
          onClose={handleCloseModal}
          onUpdate={handleUpdateRouter}
          onDelete={handleDeleteRouter}
        />
      )}
    </div>
  );
};

export default RouterTable;
